package com.bloxbean.cardano.yaci.core.model.serializers;

import com.bloxbean.cardano.yaci.core.model.certs.MoveInstataneous;
import com.bloxbean.cardano.yaci.core.util.CborSerializationUtil;
import com.bloxbean.cardano.yaci.core.util.HexUtil;
import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

class MoveInstantaneousRewardsSerializerTest {

    @Test
    void deserializeDI() {
        String cborHex = "";

        MoveInstataneous moveInstataneous = MoveInstantaneousRewardsSerializer.INSTANCE.deserializeDI(CborSerializationUtil.deserialize(HexUtil.decodeHexString(cborHex)));

        assertThat(moveInstataneous.isReserves()).isFalse();
        assertThat(moveInstataneous.isTreasury()).isTrue();
        assertThat(moveInstataneous.getAccountingPotCoin()).isNull();
        assertThat(moveInstataneous.getStakeCredentialCoinMap()).hasSize(200);
    }
}
